mapper.insert:
	INSERT INTO channel_users (
     ch_link,
     user_serial,
     notification
   ) VALUES (
    #{chLink},
    #{userSerial},
    #{notification}
   )

	INSERT INTO channel_users (
     ch_link,
     user_serial,
     notification
   ) VALUES (
    (SELECT ch_link
	FROM channels
	WHERE ws_link = #{ws_link}
	AND ch_name  = '일반'
   	),
    #{user_serial},
    1
   )

	INSERT INTO channel_users (
     ch_link,
     user_serial,
     notification
   ) VALUES (
    (SELECT ch_link
	FROM channels
	WHERE ws_link = #{ws_link}
	AND ch_name  = '랜덤'
   	),
    #{user_serial},
    1
   )

mapper.delete:
	DELETE FROM channel_users
	WHERE
    	ch_link = #{chLink}
   		AND user_serial = #{userSerial}
